package Util;import java.util.Map;import android.text.TextUtils;import android.util.Log;import org.json.JSONObject;public class PayResult {	private String resultStatus;	private String result;	private String memo;	public String code;	public String msg;	public String app_id;	public String auth_app_id;	public String charset;	public String timestamp;	public String total_amount;	public String trade_no;	public String seller_id ;	public String out_trade_no;	public PayResult(Map<String, String> rawResult) {		if (rawResult == null) {			return;		}		for (String key : rawResult.keySet()) {			if (TextUtils.equals(key, "resultStatus")) {				resultStatus = rawResult.get(key);			} else if (TextUtils.equals(key, "result")) {				result = rawResult.get(key);			} else if (TextUtils.equals(key, "memo")) {				memo = rawResult.get(key);			}		}		if(result != null){			try{				JSONObject jsonObject = new JSONObject(result);				String response = jsonObject.getString("alipay_trade_app_pay_response");				if(response != null && !response.equals("")){					JSONObject item = new JSONObject(response);					code = item.getString("code");					msg  = item.getString("msg");					app_id  = item.getString("app_id");					auth_app_id = item.getString("auth_app_id");					charset = item.getString("charset");					timestamp = item.getString("timestamp");					total_amount = item.getString("total_amount");					trade_no = item.getString("trade_no");					seller_id = item.getString("seller_id");					out_trade_no = item.getString("out_trade_no");					Log.d("lizisong", code+":"+msg+":"+app_id+":"+auth_app_id+":"+charset+":"					+timestamp+":"+total_amount+":"+trade_no+":"+seller_id+":"+out_trade_no);				}			}catch (Exception e){			}		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}}